import { Heading } from '@chakra-ui/react';
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './TodoApp.css';
import axios from 'axios';

function TodoApp() {
	const [titleValue, setTitleValue] = useState('');
	const [textValue, setTextValue] = useState('');
	const [todos, setTodos] = useState([]);

	const handleInputChange = (e) => {
		setTextValue(e.target.value);
	};
	const idno=1;
	const handleAddTodo = () => {
		if (inputValue !== '') {
			const newTodo = {
				id: idno,
				title: titleValue,
				text: textValue,
				completed: false,
			};
			idno++;
			axios.post('http://localhost:3000/todos', newTodo)
			setTodos([...todos, newTodo]);
			setInputValue('');	
		}
	};

	const handleDeleteTodo = (id) => {
		const updatedTodos = todos.filter((todo) => todo.id !== id);
		setTodos(updatedTodos);
	};

	const handleUpdateTodo = (id) => {
		const updatedTodos = todos.filter((todo) => todo.id === id);
		setTodos(updatedTodos);
	};

	const handleToggleCompleted = (id) => {
		const updatedTodos = todos.map((todo) => {
			if (todo.id === id) {
				return { ...todo, completed: !todo.completed };
			}
			return todo;
		});
		setTodos(updatedTodos);
	};

	return (
		<div className="container">
			<p className="heading">Todo App</p>
			<button onClick={handleAddTodo} style={{float:'right'}}>Add Todo</button>
			<input type="text" value={titleValue} onChange={handleInputChange} />
			<input type="text" value={textValue} onChange={handleInputChange} />
			<ul>
				{todos.map((todo) => (
					<li key={todo.id}>
						<input type="checkbox" onChange={() => handleToggleCompleted(todo.id)}/>
						<text
							style={{ textDecoration: todo.completed ? 'line-through' : 'none' }}
							onClick={() => handleToggleCompleted(todo.id)}
						>
							{todo.text}
						</text>
						<button onClick={() => handleDeleteTodo(todo.id)}>Delete</button>
						<button onClick={() => handleUpdateTodo(todo.id)}>Update</button>
					</li>
				))}
			</ul>
		</div>
	);
}

export default TodoApp;
